{
  "name": "@m42/sprint-compiler",
  "version": "1.0.0",
  "description": "Workflow compiler for m42-sprint plugin",
  "main": "dist/index.js",
  "bin": {
    "sprint-compile": "./dist/index.js",
    "sprint-status-server": "./dist/status-server/index.js"
  },
  "scripts": {
    "build": "tsc",
    "dev": "tsc --watch",
    "clean": "rm -rf dist",
    "compile": "node dist/index.js",
    "test": "npm run build && node dist/validate.test.js"
  },
  "dependencies": {
    "commander": "^12.0.0",
    "js-yaml": "^4.1.0"
  },
  "devDependencies": {
    "@types/js-yaml": "^4.0.9",
    "@types/node": "^20.11.0",
    "typescript": "^5.3.0"
  },
  "engines": {
    "node": ">=18.0.0"
  }
}
