     1→/**
     2→ * TypeScript interfaces for the Sprint Workflow System
     3→ */
     4→
     5→// ============================================================================
     6→// SPRINT.yaml - Input Format
     7→// ============================================================================
     8→
     9→/**
    10→ * A step in the sprint (formerly called "feature")
    11→ */
    12→export interface SprintStep {
    13→  /** The prompt/description for this step */
    14→  prompt: string;
    15→  /** Optional: Override the default workflow for this step */
    16→  workflow?: string;
    17→  /** Optional: Custom ID for this step */
    18→  id?: string;
    19→}
    20→
    21→/**
    22→ * Error category types for classification and retry configuration
    23→ */
    24→export type ErrorCategory = 'network' | 'rate-limit' | 'timeout' | 'validation' | 'logic';
    25→
    26→/**
    27→ * Retry configuration for automatic error recovery
    28→ */
    29→export interface RetryConfig {
    30→  /** Maximum number of retry attempts per phase */
    31→  maxAttempts: number;
    32→  /** Exponential backoff delays in milliseconds */
    33→  backoffMs: number[];
    34→  /** Error categories that should trigger automatic retry */
    35→  retryOn: ErrorCategory[];
    36→}
    37→
    38→/**
    39→ * Sprint Definition - the input format (SPRINT.yaml)
    40→ */
    41→export interface SprintDefinition {
    42→  /** Reference to the workflow in .claude/workflows/ */
    43→  workflow: string;
    44→  /** The steps to process in this sprint */
    45→  steps: SprintStep[];
    46→  /** Optional sprint metadata */
    47→  'sprint-id'?: string;
    48→  name?: string;
    49→  created?: string;
    50→  owner?: string;
    51→  /** Optional configuration */
    52→  config?: {
    53→    'max-tasks'?: number;
    54→    'time-box'?: string;
    55→    'auto-commit'?: boolean;
    56→  };
    57→  /** Optional retry configuration for error recovery */
    58→  retry?: RetryConfig;
    59→}
    60→
    61→// ============================================================================
    62→// Workflow Definition (.claude/workflows/*.yaml)
    63→// ============================================================================
    64→
    65→/**
    66→ * A phase within a workflow
    67→ */
    68→export interface WorkflowPhase {
    69→  /** Unique identifier for this phase */
    70→  id: string;
    71→  /** The prompt to execute for this phase */
    72→  prompt?: string;
    73→  /** If set to 'step', iterates over all steps from SPRINT.yaml */
    74→  'for-each'?: 'step';
    75→  /** Reference to another workflow to use for each iteration */
    76→  workflow?: string;
    77→  /** If true, this phase runs as a background parallel task */
    78→  parallel?: boolean;
    79→  /** If true, wait for all parallel tasks to complete before proceeding */
    80→  'wait-for-parallel'?: boolean;
    81→}
    82→
    83→/**
    84→ * Workflow Definition - the reusable workflow template
    85→ */
    86→export interface WorkflowDefinition {
    87→  /** Human-readable name */
    88→  name: string;
    89→  /** Description of what this workflow does */
    90→  description?: string;
    91→  /** The phases in this workflow */
    92→  phases: WorkflowPhase[];
    93→}
    94→
    95→// ============================================================================
    96→// PROGRESS.yaml - Runtime/Compiled Format
    97→// ============================================================================
    98→
    99→export type PhaseStatus = 'pending' | 'in-progress' | 'completed' | 'blocked' | 'skipped' | 'failed';
   100→export type SprintStatus = 'not-started' | 'in-progress' | 'completed' | 'blocked' | 'paused' | 'needs-human';
   101→
   102→/**
   103→ * Tracks a background parallel task spawned during sprint execution
   104→ */
   105→export interface ParallelTask {
   106→  /** Unique identifier for this parallel task */
   107→  id: string;
   108→  /** Reference to the step this task belongs to */
   109→  'step-id': string;
   110→  /** Reference to the phase this task belongs to */
   111→  'phase-id': string;
   112→  /** Current status of the parallel task */
   113→  status: 'spawned' | 'running' | 'completed' | 'failed';
   114→  /** Process ID of the background task */
   115→  pid?: number;
   116→  /** Path to the log file for this task */
   117→  'log-file'?: string;
   118→  /** ISO timestamp when the task was spawned */
   119→  'spawned-at': string;
   120→  /** ISO timestamp when the task completed */
   121→  'completed-at'?: string;
   122→  /** Exit code of the task process */
   123→  'exit-code'?: number;
   124→  /** Error message if the task failed */
   125→  error?: string;
   126→}
   127→
   128→/**
   129→ * A compiled phase (leaf node - has prompt but no sub-phases)
   130→ */
   131→export interface CompiledPhase {
   132→  id: string;
   133→  status: PhaseStatus;
   134→  prompt: string;
   135→  /** Timing information */
   136→  'started-at'?: string;
   137→  'completed-at'?: string;
   138→  elapsed?: string;
   139→  /** Any notes or summary from execution */
   140→  summary?: string;
   141→  /** Error message if phase failed */
   142→  error?: string;
   143→  /** Number of retry attempts made */
   144→  'retry-count'?: number;
   145→  /** ISO timestamp for next scheduled retry */
   146→  'next-retry-at'?: string;
   147→  /** Classified error category */
   148→  'error-category'?: ErrorCategory;
   149→  /** If true, this phase runs as a background parallel task */
   150→  parallel?: boolean;
   151→  /** Reference to the parallel task tracking this execution */
   152→  'parallel-task-id'?: string;
   153→}
   154→
   155→/**
   156→ * A compiled step (contains sub-phases from the step's workflow)
   157→ */
   158→export interface CompiledStep {
   159→  id: string;
   160→  /** The original prompt from SPRINT.yaml */
   161→  prompt: string;
   162→  status: PhaseStatus;
   163→  /** The expanded phases from the step's workflow */
   164→  phases: CompiledPhase[];
   165→  /** Timing information */
   166→  'started-at'?: string;
   167→  'completed-at'?: string;
   168→  elapsed?: string;
